void menuInit(Camera* c){
	entities[0] = newPlane(newVector3f(0,2,2), 0, 0, 0, newVector3f(0.6,0.42,0.4));
	(*c).position.z = 2 - 6.3;
	(*c).position.y = 2;
	
	prepareRenderer(*c);
}

#define ROTATE_SPEED 10
void playMenu(void){
	while(0) {				//REPLACE WITH BUTTON NOT PRESSED
		Entity* entitiesP = entities;
		render(&entitiesP, 1);
		renderGraphicsBuffer();
		
		ST7735_DrawBitmap(14, 130, Play, 100, 30);
		
		entities[0].yaw += ROTATE_SPEED;
		if(entities[0].yaw >= 360) {
			entities[0].yaw -= (360);
		}
	}
}

void difficultyMenu(Camera c) {
	initGround();
	while(entities[0].yaw < 360) {
		Entity* entitiesP = entities;
		render(&entitiesP, 1);
		renderGraphicsBuffer();

		entities[0].yaw += ROTATE_SPEED;
	}
	
	while(0) {					//While button not pressed
		//Display difficulties
		//left right input
	}
	
	//Wait for input
	float endPoint = 24;
	
	while(endPoint > 0) {
		Entity* entitiesP = entities;
		endPoint = renderMenuGround(c);
		render(&entitiesP, 1);
		renderGraphicsBuffer();
	}
}
