void soundInit(void){
	NVIC_ST_CTRL_R = 0;
	NVIC_ST_RELOAD_R = 80000000/F - 1;
	NVIC_ST_CURRENT_R = 0;
	
	NVIC_SYS_PRI3_R = (NVIC_SYS_PRI3_R&0x00FFFFFF)|0x20000000;
	NVIC_ST_CTRL_R = 0x07;
	n = 0;
	
	currentSound = NONE;
	DACInit();
}

void DACInit(void){
	uint8_t delay = 0;
	SYSCTL_RCGCGPIO_R |= 0x2;
	delay ++;
	
	GPIO_PORTB_DEN_R |= 0x3F;
	GPIO_PORTB_DIR_R |= 0x3F;
	GPIO_PORTB_DR8R_R |= 0x3F;
}

void startSound(enum soundList name){
	if(n == 0){
		//NVIC_ST_CTRL_R |= 0x1;
	
		currentSound = name;
		n = 0;	
		//DACOut(soundToPlay.sounds[0]);
	}
}

void stopSound(void){
	currentSound = NONE;
	n = 0;
}

void SysTick_Handler(void){
	//mBeat();
	
	switch (currentSound){
		case NONE:
			stopSound();
			break;
		
		case LASER:
			if(n < lsr.length-1){
				n++;
				DACOut(lsr.sounds[n]);
			}
			else
				stopSound();
			break;
	}
	
}

void DACOut(uint8_t data){
	GPIO_PORTB_DATA_R = data;
}
